body {
    background-color: #000;
}

@keyframes roundandround {
  to {
    transform:  rotateY(360deg);
  }
}

@keyframes show {
  to {
    opacity: 1;
  }
}

.scene {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 37.5em;
    height:37.5em;
    margin:2% auto;
    perspective: 16em;
}
.wrapper {
    width:100%;
    height:100%;
    transform: rotateX(45deg)  rotateY(45deg);
    transform-style: preserve-3d;
}
.ball {
    position: relative;
    width: 100%;
    height: 100%;
    margin:0 auto;
    transform-style: preserve-3d;
    animation: roundandround 30s 1.3s infinite linear;
}
.ball .ring {
    list-style: none;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border: 0.625em;
    border-style: solid;
    border-radius: 50%;
    opacity: 0;
    animation: show 0.75s forwards ease-in-out;
}

